mapper.select:
SELECT * FROM sys_tenant


        AND tenant_name LIKE CONCAT('%', #{name}, '%')


        AND tenant_code LIKE CONCAT('%', #{code}, '%')


        AND status = #{status}


        AND create_by = #{packageType}


        AND deleted = #{deleted}


        AND deleted = 0


ORDER BY id DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM sys_tenant


        AND tenant_name LIKE CONCAT('%', #{name}, '%')


        AND tenant_code LIKE CONCAT('%', #{code}, '%')


        AND status = #{status}


        AND create_by = #{packageType}


        AND deleted = #{deleted}


        AND deleted = 0
